import React, {useState} from "react";
import {useNavigate} from "react-router-dom";
import {Button, Table, Container} from "react-bootstrap";
import axios from "axios";
import StarRating from "../component/StarRating";

let Write = () => {
    let [inputs, setInputs] = useState({
        title: '',
        score: 0,
        content: ''
    })

    let navigate = useNavigate()
    let [rate, onSetRate] = useState(0);


    let onChange = (e) => {
        let {name, value} = e.target
        setInputs({
            ...inputs, [name]: value
        })
    }

    let onSubmit = async (e) => {
        e.preventDefault()
        try{
            let resp = await axios.post('http://localhost:8080/reply/write', inputs)
        } catch(e){
            console.log(e);

        }

    }


    return (
        <Container>
            <form className={"m-3"} onSubmit={onSubmit}>
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th colSpan={5}>[리뷰 작성하기]</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td colSpan={2}>호텔은 만족하셨나요?</td>
                        <td className={'star_wrap'}>
                            <StarRating max={5} onSetRate={onSetRate} defaultRate={rate}/>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>만족도 {rate}점을 주셨네요.</td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>리뷰를 작성해주세요.</td>
                        <td></td>
                    </tr>
                    <tr>
                        <td>제목</td>
                        <td>
                            <input type={'text'} value={inputs.title} name={'title'} onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td>내용</td>
                        <td>
                            <textarea name={'content'} value={inputs.content} className={'form-control'}
                                      onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>
                            <Button type={'submit'}>등록하기</Button>
                        </td>
                    </tr>
                    </tbody>
                </Table>
            </form>
        </Container>
    )
}

export default Write;
