import "react-bootstrap";
import {Button, Container, Table} from "react-bootstrap";
import React, {useEffect, useState} from "react";
import axios from "axios";
import {useNavigate, useParams} from "react-router-dom";

let ReplyList = () => {
    let params = useParams(2);
    let [data, setData] = useState({replyList: []})
    let navigate = useNavigate();
    let id = parseInt(params.id);


    useEffect ( () => {
        const selectList = async () => {
            // 이부분에 메인에서 넘어오는 호텔 Id 를 받아오도록 연결 시에 수정해야함
            let resp = await axios
                .get('http://localhost:8080/reply/selectList');

            if (resp.status === 200) {
                setData(resp.data)
            }
        }
        selectList();
    }, [])

    let onUpdate = () => {
        navigate('/reply/update/' + 1)
    }

    let onDelete = async () => {
        let response = await axios.post('/http://localhost:8080/reply/delete/' + id, {});
        // user 정보 가지고 계속 움직여야 함.
        if(response.status === 200){
            navigate('/reply/selectList/1')
        }
    }

// ----------------- Table -------------------------

    let dateStyle = {
     fontSize: '12px',
    }

    let TableRow = ({reply}) => {
        return (
        /*    <tr>
                <td>{reply.id}</td>
                <td>{reply.title}</td>
                <td>{reply.score}</td>
                <td>{reply.content}</td>
                <td style={dateStyle}>{reply.entryDate}</td>
                <td style={dateStyle}>{reply.modifyDate}</td>
                    {/!*if 문 넣어서 userId 와 같을때 조건문 만들기 *!/}
                    <td>
                        <Button onClick={onUpdate}>수정</Button>
                        <Button onClick={onDelete}>삭제</Button>
                    </td>
            </tr>*/
            <div>
                <span>{reply.id}</span>
                <span>{reply.title}</span>
                <span>{reply.score}</span>
                <span>{reply.content}</span>
                <span>{reply.entryDate}</span>
                <span>{reply.modifyDate}</span>
                <div>
                    <Button onClick={onUpdate}>수정</Button>
                    <Button onClick={onDelete}>삭제</Button>
                </div>
            </div>
        )
    }

    let moveToWrite = () => {
        navigate('/reply/write')
    }

    return (
        <Container>
           {/* <Table striped bordered hover>
                <thead>
                <tr>
                    <td>
                        <Button type={'button'} onClick={moveToWrite}>댓글쓰기</Button>
                    </td>
                </tr>
                <tr>
                    <th>글쓴이</th>
                    <th>제목</th>
                    <th>별점</th>
                    <th>내용</th>
                    <th>리뷰 게시 날짜</th>
                    <th>리뷰 수정일</th>
                </tr>
                </thead>
                <tbody>
                {data.replyList.map(r => (
                    <TableRow reply={r} key={r.id}/>
                    ))}
                </tbody>
            </Table>*/}
            <ul>
                <li>
                    <Button type={'button'} onClick={moveToWrite}>댓글쓰기</Button>
                </li>
                <li>
                    {data.replyList.map(r => (
                    <TableRow reply={r} key={r.id}/>
                    ))}
                </li>
            </ul>
        </Container>
    )
}

export default ReplyList;
