import Header from "../component/Header";
import Footer from "../component/Footer";
import { Button, Col, Container, Image } from "react-bootstrap";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

// 날짜 포맷 함수
const modifyDate = (dateString) => {
    let date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
        year: '2-digit',
        month: '2-digit',
        day: '2-digit'
    }).replace('.', '년 ').replace('.', '월 ').replace('.', '일');
};

// 방 종류 텍스트 반환 함수
const getRoomMemberText = (roomMember) => {
    switch (roomMember) {
        case 1: return '싱글룸';
        case 2: return '더블룸';
        case 3: return '트윈룸';
        case 4: return '트리플룸';
        case 5: return '스위트룸';
        default: return '알 수 없음';
    }
};

// 룸 타입 텍스트 반환 함수
const getRoomTypeText = (roomType) => {
    switch (roomType) {
        case 1: return '스탠다드';
        case 2: return '슈페리어';
        case 3: return '디럭스';
        case 4: return '이그제큐티브';
        default: return '알 수 없음';
    }
};

const Details = () => {
    const [data, setData] = useState({});
    const [images, setImages] = useState([]);
    const params = useParams();
    const id = parseInt(params.id);
    const location = useLocation();
    const { userInfo } = location.state || {};
    const navigate = useNavigate();

    // 스타일 설정
    const profileListStyle = {
        listStyleType: 'none',
        width: '100%',
        height: '260px',
        borderTop: '1px solid #999',
        borderBottom: '1px solid #999'
    };

    const listStyle = {
        listStyleType: 'none',
        width: '100%',
        borderTop: '1px solid #999',
        borderBottom: '1px solid #999',
        paddingTop: '15px',
        paddingBottom: '15px'
    };

    const size = {
        width: '100%',
        height: '300px'
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const hotelResp = await axios.get(`http://localhost:8080/hotel/showOne/${id}`, {
                    withCredentials: true
                });
                if (hotelResp.status === 200) {
                    setData(hotelResp.data);

                    const imagesResp = await axios.get(`http://localhost:8080/hotel/uploads/${hotelResp.data.id}/${hotelResp.data.roomNumber}`, {
                        withCredentials: true
                    });
                    if (imagesResp.status === 200) {
                        setImages(imagesResp.data);
                    }
                }
            } catch (e) {
                console.error('호텔 정보 가져오기 오류:', e);
            }
        };
        fetchData();
    }, [id]);

    const goBack = () => {
        navigate('/hotel/showList/1', { state: { userInfo } });
    };

    const onUpdate = () => {
        navigate('/hotel/update/' + id, { state: { userInfo } });
    };

    const onLogout = async () => {
        const response = await axios.post('http://localhost:8080/user/logOut', {}, {
            withCredentials: true
        });

        if (response.status === 200) {
            navigate('/');
        }
    };

    const onDelete = async () => {
        const response = await axios.get(`http://localhost:8080/hotel/delete/${id}`, {
            withCredentials: true
        });

        if (response.status === 200) {
            navigate('/hotel/showList/1', { state: { userInfo } });
        }
    };

    const isAdmin = userInfo?.role === 'role_admin';
    const firstImage = images.length > 0 ? `http://localhost:8080/hotel/uploads/${data.id}/${data.roomNumber}/${images[0]}` : process.env.PUBLIC_URL + '/logo_img.png';

    return (
        <>
            <Header userInfo={userInfo} />
            <Container className="mt-3">
                <section>
                    <div id="hotelProfile" className="d-flex align-items-center mb-3">
                        <div className="me-3">
                            <img
                                src={firstImage}
                                alt="호텔 이미지"
                                style={{ width: '280px', height: 'auto' }}
                            />
                        </div>
                        <div className="flex-grow-1">
                            <h2>호텔명: {data.name}</h2>
                            <p>글번호: {data.id}</p>
                            <p>작성자: {data.nickname}</p>
                            <p>주소: {data.address}</p>
                            <p>시작 일자: {modifyDate(data.startEntry)}</p>
                            <p>종료 일자: {modifyDate(data.endEntry)}</p>
                            <p>방 넘버: {data.roomNumber}</p>
                            <p>방 종류: {getRoomMemberText(data.roomMember)}</p>
                            <p>룸 타입: {getRoomTypeText(data.roomType)}</p>
                            <p>가격: {data.price}</p>
                            <p>내용: {data.content}</p>
                        </div>
                        {data && (
                            <ul style={profileListStyle}>
                                <li className={'p-2'}>{data.name}</li>
                                <li className={'p-2'}>{data.content}</li>
                                <li className={'p-2'}>{modifyDate(data.startEntry)} ~ {modifyDate(data.endEntry)}</li>
                                <li className={'p-2'}>{data.content}</li>
                                <li className={'p-2'}>{data.content}</li>

                                <li className={'p-2'}>
                                    {/* 버튼에 클릭 핸들러 추가 */}
                                    <Button>버튼1</Button>
                                    <Button>장바구니</Button>
                                    <Button>예약하기</Button>
                                </li>
                            </ul>
                        )}

                    </div>
                    {images.length > 0 && (
                        <div className="mb-3">
                            <h4>호텔 이미지</h4>
                            <div className="d-flex flex-wrap">
                                {images.map((filename, index) => (
                                    <Col xs={12} md={6} lg={4} key={index} className="mb-2">
                                        <Image
                                            src={`http://localhost:8080/hotel/uploads/${data.id}/${data.roomNumber}/${filename}`}
                                            alt={`호텔 이미지 ${filename}`}
                                            fluid
                                            className="w-100"
                                            style={{ height: '300px' }}
                                        />
                                    </Col>
                                ))}
                            </div>
                        </div>
                    )}
                    <aside>
                        {/* 리뷰 */}
                        <div style={{ marginTop: '20px', marginBottom: '20px' }}>
                            <h3>최고의 경험이었습니다!!</h3>
                            <p>score 5.0</p>
                            <Button onClick={() => navigate('/reply/selectList')} size="sm">호텔리뷰 더보기</Button>
                        </div>
                    </aside>
                    <article>
                        {/* 상세보기 버튼 만들기 */}
                        <ul style={listStyle}>
                            <li className={'d-flex justify-content-center align-items-center'}>
                                <Button>상세보기</Button>
                                <Button className={'mx-4'}>개요</Button>
                            </li>
                        </ul>
                    </article>
                    <figure>
                        {/* 상세보기 보여줄 것 */}
                        <Container className={'d-flex flex-column mb-3 justify-content-center'}>
                            {data.images && data.images.map((img, index) => (
                                <Col xs={12} md={6} lg={4} key={index}>
                                    <Image src={`http://localhost:8080/hotel/uploads/${data.id}/${img}`} thumbnail style={size} />
                                </Col>
                            ))}
                        </Container>
                    </figure>
                    {isAdmin && (
                        <div className="mb-3">
                            <Button onClick={onDelete}>삭제하기</Button>
                        </div>
                    )}
                    {data.sellerId === userInfo?.id && !isAdmin && (
                        <div className="mb-3">
                            <Button onClick={onUpdate}>수정하기</Button>
                            <Button onClick={onDelete} className="ms-2">삭제하기</Button>
                        </div>
                    )}
                    <div className="text-center">
                        <Button onClick={goBack}>뒤로 가기</Button>
                    </div>
                </section>
            </Container>
            <Footer />
        </>
    );
};

export default Details;
