import {Button, Container, FormControl, Table} from "react-bootstrap";
import React, {useState} from "react";
import axios from "axios";
import StarRating from "../component/StarRating";

let Update = () => {
    let update = async() => {
        let resp = await axios.post('https://localhost:8080/reply/update/' + 1, {})

    }

    let [inputs, setInputs] = useState({
        title: '',
        content:'',
        score: 0
    })

    let onChange = (e) => {
        let {name, value} = e.target;
        setInputs({
            ...inputs,
            [name]: value
        })
    }

    let onSubmit = () => {

    }

    let getDataFromChild = (data) =>{
        setInputs({
            ...inputs,
            score: data
        })
    }

    return(
        <Container>
            <form className={"m-3"} onSubmit={onSubmit}>
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th>[리뷰 작성하기]</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>호텔은 만족하셨나요?</td>
                        <td>
                            <StarRating max={5} setInputs={getDataFromChild} defaultRate={inputs.score}
                                        value={inputs.score}/>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>리뷰를 작성해주세요.</td>
                    </tr>
                    <tr>
                        <td>제목</td>
                        <td>
                            <input type={'text'} value={inputs.title} name={'title'} onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td>내용</td>
                        <td>
                            <FormControl name={'content'} value={inputs.content}
                                         onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td className={'text-center'}>
                            <Button type={'submit'}>등록하기</Button>
                        </td>
                    </tr>
                    </tbody>
                </Table>
            </form>
        </Container>
    )
}

export default Update
