import React, {useState} from "react";
import "react-bootstrap";
import {Button, Table, Container, FormControl} from "react-bootstrap";
import axios from "axios";
import StarRating from "../component/StarRating";

let Write = () => {

    let [inputs, setInputs] = useState({
        title: '',
        score: 0,
        content: ''
    })

    let getDataFromChild = (data) =>{
        setInputs({
            ...inputs,
            score: data
        });
    }

    let onChange = (e) => {
        let {name, value} = e.target
        setInputs({
            ...inputs,
            [name]: value
        })
    }

    let onSubmit = async (e) => {
        e.preventDefault()

        try {
            let resp = await axios.post('http://localhost:8080/reply/write', inputs, {})

        } catch (error){
            window.alert("접속실패 다시 시도하여 주시기 바랍니다!", );
        }
    }

    return (
        <Container>
            <form className={"m-3"} onSubmit={onSubmit}>
                <Table striped bordered hover>
                    <thead>
                    <tr>
                        <th colSpan={2}>[리뷰 작성하기]</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>호텔은 만족하셨나요?</td>
                        <td>
                            <StarRating max={5} setInputs={getDataFromChild} defaultRate={inputs.score}
                                        value={inputs.score}/>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>만족도 {inputs.score}점을 주셨네요.</td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>리뷰를 작성해주세요.</td>
                    </tr>
                    <tr>
                        <td>제목</td>
                        <td>
                            <input type={'text'} value={inputs.title} name={'title'} onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td>내용</td>
                        <td>
                            <FormControl name={'content'} value={inputs.content}
                                      onChange={onChange}/>
                        </td>
                    </tr>
                    <tr>
                        <td colSpan={2} className={'text-center'}>
                            <Button type={'submit'}>등록하기</Button>
                        </td>
                    </tr>
                    </tbody>
                </Table>
            </form>
        </Container>
    )
}

export default Write;
