import {Button, Stack} from "react-bootstrap";

let Nav = ({scrollRef}) => {

    let scrollToItem = (text) => {
            if(text === "호텔상품"){
                scrollRef.current[1].scrollIntoView({behavior: 'smooth'});
            }

            if(text === "인기상품"){
                scrollRef.current[2].scrollIntoView({behavior: 'smooth'});
            }
    }

    return (
        <nav>
            <Stack direction="horizontal" className="d-sm-flex justify-content-center align-items-center">
                <Button onClick={scrollToItem} text="호텔상품" ref={scrollRef.current[1]}  size={"sm"}> 호텔상품 보기 </Button>
                <Button onClick={scrollToItem} text="인기상품" ref={scrollRef.current[2]} size={"sm"} className={'m-lg-3'}> 인기상품 보기 </Button>
            </Stack>
        </nav>
    )
}

export default Nav;
