import { Container, Pagination, Button, Image, Form, Card } from "react-bootstrap";
import { useEffect, useState, createContext, useRef } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Header from "../component/Header";
import Footer from "../component/Footer";
import Banner from "../component/Banner";
import PopularHotel from "../component/PopularHotel";
import Nav from "../component/Nav";

let refContext = createContext(null);

let Main = () => {
    const location = useLocation();
    let userInfo = location.state?.userInfo;
    let params = useParams();
    let pageNo = params.pageNo || 1;
    const popularRef = useRef(null);

    const [data, setData] = useState({ hotelList: [] });
    const [searchType, setSearchType] = useState('name');
    const [keyword, setKeyword] = useState('');
    const [minPrice, setMinPrice] = useState('');
    const [maxPrice, setMaxPrice] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [isSearching, setIsSearching] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (!userInfo || !userInfo.role) {
            return;
        }
    }, [userInfo]);

    let moveToSingle = (id) => {
        navigate('/hotel/showOne/' + id, { state: { userInfo: userInfo } });
    };

    let moveToPage = (pageNo) => {
        navigate(`/hotel/showList/${pageNo}?searchType=${searchType}&keyword=${keyword}&minPrice=${minPrice}&maxPrice=${maxPrice}&startDate=${startDate}&endDate=${endDate}`, { state: { userInfo: userInfo } });
    };

    useEffect(() => {
        let selectList = async () => {
            try {
                const params = {
                    searchType: searchType || undefined,
                    keyword: keyword || undefined,
                    minPrice: minPrice || undefined,
                    maxPrice: maxPrice || undefined,
                    startDate: startDate || undefined,
                    endDate: endDate || undefined
                };

                let resp = await axios.get(`http://localhost:8080/hotel/showList/${pageNo}`, {
                    params: isSearching ? Object.fromEntries(Object.entries(params).filter(([_, v]) => v !== undefined)) : {},
                    withCredentials: true
                });
                if (resp.status === 200) {
                    const hotelList = await Promise.all(resp.data.hotelList.map(async (hotel) => {
                        try {
                            const imagesResp = await axios.get(`http://localhost:8080/hotel/uploads/${hotel.id}/${hotel.roomNumber}`, {
                                withCredentials: true
                            });
                            return {
                                ...hotel,
                                images: imagesResp.status === 200 ? imagesResp.data : []
                            };
                        } catch (imageError) {
                            console.error('이미지 가져오기 오류:', hotel.id, imageError);
                            return { ...hotel, images: [] };
                        }
                    }));
                    setData({ ...resp.data, hotelList });
                }
            } catch (e) {
                console.error('호텔 리스트 가져오기 오류:', e);
            }
        };
        selectList();
    }, [pageNo, searchType, keyword, minPrice, maxPrice, startDate, endDate, isSearching]);

    let isSeller = userInfo?.role === 'role_seller';

    let moveToWrite = () => {
        navigate('/hotel/write', { state: { userInfo: userInfo } });
    };

    const handleSearchTypeChange = (e) => setSearchType(e.target.value);
    const handleKeywordChange = (e) => setKeyword(e.target.value);
    const handleMinPriceChange = (e) => setMinPrice(e.target.value);
    const handleMaxPriceChange = (e) => setMaxPrice(e.target.value);
    const handleStartDateChange = (e) => setStartDate(e.target.value);
    const handleEndDateChange = (e) => setEndDate(e.target.value);

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        setIsSearching(true);
        moveToPage(1);
    };

    const handleReset = () => {
        setSearchType('name');
        setKeyword('');
        setMinPrice('');
        setMaxPrice('');
        setStartDate('');
        setEndDate('');
        setIsSearching(false);
        moveToPage(1);
    };

    return (
        <refContext.Provider value={{ popularRef }}>
            <Header userInfo={userInfo} />
            <Nav />
            <Container fluid style={{ width: '80%' }}>
                <Form className="mt-4" onSubmit={handleSearchSubmit}>
                    <div className="row mb-3">
                        <div className="col-md-3">
                            <Form.Select value={searchType} onChange={handleSearchTypeChange}>
                                <option value="name">호텔명</option>
                                <option value="address">주소</option>
                            </Form.Select>
                        </div>
                        <div className="col-md-3">
                            <Form.Control
                                type="text"
                                placeholder="검색어를 입력하세요"
                                value={keyword}
                                onChange={handleKeywordChange}
                            />
                        </div>
                        <div className="col-md-2">
                            <Form.Control
                                type="number"
                                placeholder="최소 가격"
                                value={minPrice}
                                onChange={handleMinPriceChange}
                            />
                        </div>
                        <div className="col-md-2">
                            <Form.Control
                                type="number"
                                placeholder="최대 가격"
                                value={maxPrice}
                                onChange={handleMaxPriceChange}
                            />
                        </div>
                    </div>
                    <div className="row mb-3">
                        <div className="col-md-3">
                            <Form.Control
                                type="date"
                                value={startDate}
                                onChange={handleStartDateChange}
                            />
                        </div>
                        <div className="col-md-3">
                            <Form.Control
                                type="date"
                                value={endDate}
                                onChange={handleEndDateChange}
                            />
                        </div>
                        <div className="col-md-2">
                            <Button className="btn btn-outline-primary" type="submit">검색</Button>
                        </div>
                        <div className="col-md-2">
                            <Button className="btn btn-outline-secondary" type="button" onClick={handleReset}>초기화</Button>
                        </div>
                        {isSeller && (
                            <div className="col-md-2">
                                <Button className="btn btn-outline-success" onClick={moveToWrite}>글 작성하기</Button>
                            </div>
                        )}
                    </div>
                </Form>

                <Banner />
                <PopularHotel max={10} id={'popular'} ref={popularRef} />

                <div className="row">
                    {data.hotelList.length > 0 ? (
                        data.hotelList.map(hotel => (
                            <div className="col-md-4 mb-4" key={hotel.id}>
                                <HotelCard hotel={hotel} moveToSingle={moveToSingle} />
                            </div>
                        ))
                    ) : (
                        <div className="col-12 text-center">호텔 리스트가 없습니다.</div>
                    )}
                </div>
                <div className="text-center">
                    <MyPagination
                        startPage={data.startPage}
                        endPage={data.endPage}
                        currentPage={data.currentPage}
                        maxPage={data.maxPage}
                        moveToPage={moveToPage}
                    />
                </div>
            </Container>
            <Footer />
        </refContext.Provider>
    );
}

let HotelCard = ({ hotel, moveToSingle }) => {
    const thumbnailUrl = hotel.images && hotel.images.length > 0
        ? `http://localhost:8080/hotel/uploads/${hotel.id}/${hotel.roomNumber}/${hotel.images[Math.floor(Math.random() * hotel.images.length)]}`
        : '';

    return (
        <Card onClick={() => moveToSingle(hotel.id)} style={{ cursor: 'pointer' }}>
            <Card.Img variant="top" src={thumbnailUrl} style={{ height: '180px', objectFit: 'cover' }} />
            <Card.Body>
                <Card.Title>{hotel.name}</Card.Title>
                <Card.Text>
                    <strong>오너:</strong> {hotel.nickname}<br />
                    <strong>설명:</strong> {hotel.shortContent}
                </Card.Text>
            </Card.Body>
        </Card>
    );
};

let MyPagination = ({ startPage, endPage, currentPage, maxPage, moveToPage }) => {
    let items = [];
    items.push(
        <Pagination.First key="first" onClick={() => moveToPage(1)} />
    );
    for (let i = startPage; i <= endPage; i++) {
        items.push(
            <Pagination.Item key={i} active={i === currentPage} onClick={() => moveToPage(i)}>
                {i}
            </Pagination.Item>
        );
    }
    items.push(
        <Pagination.Last key="last" onClick={() => moveToPage(maxPage)} />
    );
    return (
        <Pagination className={"justify-content-center"}>
            {items}
        </Pagination>
    );
};

export default Main;
