import Header from "../component/Header";
import Footer from "../component/Footer";
import { Button, Col, Container, Image, Table } from "react-bootstrap";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import axios from 'axios';
import { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

const modifyDate = (dateString) => {
    let date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
        year: '2-digit',
        month: '2-digit',
        day: '2-digit'
    }).replace('.', '년 ').replace('.', '월 ').replace('.', '일');
};

const getRoomMemberText = (roomMember) => {
    switch (roomMember) {
        case 1: return '싱글룸';
        case 2: return '더블룸';
        case 3: return '트윈룸';
        case 4: return '트리플룸';
        case 5: return '스위트룸';
        default: return '알 수 없음';
    }
};

const getRoomTypeText = (roomType) => {
    switch (roomType) {
        case 1: return '스탠다드';
        case 2: return '슈페리어';
        case 3: return '디럭스';
        case 4: return '이그제큐티브';
        default: return '알 수 없음';
    }
};

const Details = () => {
    const [data, setData] = useState({});
    const [images, setImages] = useState([]);
    const params = useParams();
    const id = parseInt(params.id);
    const location = useLocation();
    const userInfo = location.state?.userInfo;
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const hotelResp = await axios.get(`http://localhost:8080/hotel/details/${id}`, {
                    withCredentials: true
                });
                if (hotelResp.status === 200) {
                    setData(hotelResp.data);

                    const imagesResp = await axios.get(`http://localhost:8080/hotel/uploads/${hotelResp.data.id}/${hotelResp.data.roomNumber}`, {
                        withCredentials: true
                    });
                    if (imagesResp.status === 200) {
                        setImages(imagesResp.data);
                    }
                }
            } catch (e) {
                console.error(e);
            }
        };
        fetchData();
    }, [id]);

    const goBack = () => {
        navigate('/', { state: { userInfo: userInfo } });
    };

    const onUpdate = () => {
        navigate('/hotel/update/' + id, { state: { userInfo: userInfo } });
    };

    const onLogout = async () => {
        try {
            const response = await axios.post('http://localhost:8080/user/logOut', {}, {
                withCredentials: true
            });

            if (response.status === 200) {
                navigate('/');
            }
        } catch (e) {
            console.error(e);
        }
    };

    const onDelete = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/hotel/delete/${id}`, {
                withCredentials: true
            });

            if (response.status === 200) {
                navigate('/', { state: { userInfo: userInfo } });
            }
        } catch (e) {
            console.error(e);
        }
    };

    const isAdmin = userInfo?.role === 'role_admin';

    // 추가: data와 images가 로드되기 전에 렌더링 방지
    if (!data || !images) {
        return <div>로딩 중...</div>;
    }

    return (
        <>
            <Header />
            <Container className="justify-content-center mt-3">
                <section>
                    <div id="hotelProfile" className="d-flex align-items-center">
                        <div>
                            <img src={process.env.PUBLIC_URL + '/logo_img.png'} alt="로고" style={{ width: '280px', height: 'auto' }} />
                        </div>
                        <ul style={{ listStyleType: 'none', width: '100%', height: '260px', borderTop: '1px solid #999', borderBottom: '1px solid #999' }}>
                            <li className="p-2">호텔명: {data.name}</li>
                            <li className="p-2">주소: {data.address}</li>
                            <li className="p-2">시작 일자: {modifyDate(data.startEntry)}</li>
                            <li className="p-2">종료 일자: {modifyDate(data.endEntry)}</li>
                            <li className="p-2">방 넘버: {data.roomNumber}</li>
                            <li className="p-2">주소: {data.address}</li>
                            <li className="p-2">주소: {data.address}</li>
                            <li className="p-2">가격: {data.price}</li>
                            <li className="p-2">
                                <span>리뷰 점수</span>
                                <p>해당 평점 관련 후기 클릭</p>
                            </li>
                            <li className="p-2">
                                <Button>버튼1</Button>
                                <Button>장바구니</Button>
                                <Button>예약하기</Button>
                            </li>
                        </ul>
                    </div>
                    <aside style={{ marginTop: '20px', marginBottom: '20px' }}>
                        <h3>최고의 경험이었습니다!!</h3>
                        <p>score 5.0</p>
                        <Button onClick={() => navigate('/reply/selectList')} size="sm">호텔리뷰 더보기</Button>
                    </aside>
                    <article>
                        <ul style={{ listStyleType: 'none', width: '100%', borderTop: '1px solid #999', borderBottom: '1px solid #999', paddingTop: '15px', paddingBottom: '15px' }}>
                            <li className="d-flex justify-content-center align-items-center">
                                <Button>상세보기</Button>
                                <Button className="mx-4">개요</Button>
                            </li>
                        </ul>
                    </article>
                    <figure>
                        <Container className="d-flex flex-column mb-3 justify-content-center">
                            {images.map((filename) => (
                                <Col key={filename} xs={8} sm={6}>
                                    <Image
                                        src={`http://localhost:8080/hotel/uploads/${data.id}/${data.roomNumber}/${filename}`}
                                        alt={`호텔 이미지 ${filename}`}
                                        fluid
                                        thumbnail
                                        style={{ width: '100%', height: '300px' }}
                                    />
                                </Col>
                            ))}
                        </Container>
                    </figure>

                </section>
            </Container>
            <Footer />
        </>
    );
};

export default Details;
