import {Button, Stack} from "react-bootstrap";
import {useRef} from "react";

let Nav = ({scrollRef}) => {
    let targetRef = useRef([]);

    let scrollToItem = (text) => {
            if(text === "호텔상품"){
                targetRef.current[1]();
            }

            if(text === "인기상품"){
                targetRef.current[2]();
            }
    }

    return (
        <nav>
            <Stack direction="horizontal" className="d-sm-flex justify-content-center align-items-center">
                <Button onClick={scrollRef} text="호텔상품" ref={targetRef.current[1]}  size={"sm"}> 호텔상품 보기 </Button>
                <Button onClick={scrollRef} text="인기상품" ref={targetRef.current[2]} size={"sm"} className={'m-lg-3'}> 인기상품 보기 </Button>
            </Stack>
        </nav>
    )
}

export default Nav;
