import Header from "../component/Header";
import Footer from "../component/Footer";
import { Button, Col, Container, Image } from "react-bootstrap";
import { useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

let Details = () => {
    let profileListStyle = {
        listStyleType: 'none',
        width: '100%',
        height: '260px',
        borderTop: '1px solid #999',
        borderBottom: '1px solid #999'
    };

    let listStyle = {
        listStyleType: 'none',
        width: '100%',
        borderTop: '1px solid #999',
        borderBottom: '1px solid #999',
        paddingTop: '15px',
        paddingBottom: '15px'
    };

    let size = {
        width: '100%',
        height: '300px',
    };

    let navigate = useNavigate();
    let location = useLocation();
    let { userInfo, hotelId } = location.state || {};

    const [hotel, setHotel] = useState(null);

    useEffect(() => {
        if (hotelId) {
            const fetchHotel = async () => {
                try {
                    let response = await axios.get(`http://localhost:8080/hotel/showOne/${hotelId}`, {
                        withCredentials: true
                    });
                    if (response.status === 200) {
                        setHotel(response.data);
                    }
                } catch (error) {
                    console.error('호텔 정보 가져오기 오류:', error);
                }
            };
            fetchHotel();
        }
    }, [hotelId]);

    const moveToPage = () => {
        navigate('/reply/selectList');
    };

    return (
        <>
            <Header userInfo={userInfo} />
            <Container className={'justify-content-center'}>
                <section>
                    <div id={'hotelProfile'} className={'d-flex align-items-center'}>
                        <div>
                            <img src={process.env.PUBLIC_URL + '/logo_img.png'} alt="로고임 사진 바꿔야함" style={{
                                width: '280px',
                                height: 'auto'
                            }} />
                        </div>
                        {hotel && (
                            <ul style={profileListStyle}>
                                <li className={'p-2'}>{hotel.title}</li>
                                <li className={'p-2'}>{hotel.content}</li>
                                <li className={'p-2'}>
                                    <span>Review Score: {hotel.reviewScore}</span>
                                    <p>해당 평점 관련 후기 클릭</p>
                                </li>
                                <li className={'p-2'}>
                                    {/* 버튼에 클릭 핸들러 추가 */}
                                    <Button>버튼1</Button>
                                    <Button>장바구니</Button>
                                    <Button>예약하기</Button>
                                </li>
                            </ul>
                        )}
                    </div>
                    <aside>
                        {/* 리뷰 */}
                        <div style={{ marginTop: '20px', marginBottom: '20px' }}>
                            <h3>최고의 경험이었습니다!!</h3>
                            <p>score 5.0</p>
                            <Button onClick={moveToPage} size="sm">호텔리뷰 더보기</Button>
                        </div>
                    </aside>
                    <article>
                        {/* 상세보기 버튼 만들기 */}
                        <ul style={listStyle}>
                            <li className={'d-flex justify-content-center align-items-center'}>
                                <Button>상세보기</Button>
                                <Button className={'mx-4'}>개요</Button>
                            </li>
                        </ul>
                    </article>
                    <figure>
                        {/* 상세보기 보여줄 것 */}
                        <Container className={'d-flex flex-column mb-3 justify-content-center'}>
                            {hotel && hotel.images && hotel.images.map((img, index) => (
                                <Col xd={8} ms={6} key={index}>
                                    <Image src={`http://localhost:8080/hotel/uploads/${hotel.id}/${img}`} thumbnail style={size} />
                                </Col>
                            ))}
                        </Container>
                    </figure>
                </section>
            </Container>
            <Footer />
        </>
    );
};

export default Details;
